import { useState } from "react";
import "./App.css"; // vamos criar esse arquivo para os estilos

export default function App() {
  const [email, setEmail] = useState("");
  const [senha, setSenha] = useState("");
  const [mensagem, setMensagem] = useState("");

  const usuariosValidos = [
    { email: "eduardo.lino@pucpr.br", senha: "123456" }
  ];

  function validarAcesso() {
    const e = email.trim().toLowerCase();
    const s = senha;

    const autenticado = usuariosValidos.some(
      (u) => u.email.toLowerCase() === e && u.senha === s
    );

    if (autenticado) {
      setMensagem("Acessado com sucesso!");
    } else {
      setMensagem("Usuário ou senha incorretos!");
    }
  }

  return (
    <div className="container">
      <h1>Login</h1>
      <div className="form">
        <input
          type="email"
          placeholder="eduardo.lino@pucpr.br"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="******"
          value={senha}
          onChange={(e) => setSenha(e.target.value)}
        />
        <button onClick={validarAcesso}>Acessar</button>
      </div>
      <p className="mensagem">{mensagem}</p>
    </div>
  );
}
