import { useState } from "react";

// ✅ Requisitos da atividade:
// - Título "Login"
// - Inputs: e-mail e senha
// - Botão "Acessar"
// - Label com a mensagem de sucesso/erro
// - Validação: email === "eduardo.lino@pucpr.br" e senha === "123456"
// - (Usando array de usuários para aplicar o conteúdo visto na semana)

export default function App() {
  const [email, setEmail] = useState("");
  const [senha, setSenha] = useState("");
  const [mensagem, setMensagem] = useState("");

  // Array de credenciais válidas (poderia haver mais de um usuário)
  const usuariosValidos = [
    { email: "eduardo.lino@pucpr.br", senha: "123456" }
    // Você pode adicionar outros usuários aqui se quiser
  ];

  function validarAcesso() {
    const e = email.trim().toLowerCase();
    const s = senha;

    const autenticado = usuariosValidos.some(
      (u) => u.email.toLowerCase() === e && u.senha === s
    );

    if (autenticado) {
      setMensagem("Acessado com sucesso!");
    } else {
      setMensagem("Usuário ou senha incorretos!");
    }
  }

  return (
    <div className="min-h-screen flex items-start sm:items-center justify-center p-6">
      <div className="w-full max-w-sm">
        <h1 className="text-3xl font-bold mb-4">Login</h1>

        <div className="flex flex-col gap-3">
          <input
            type="email"
            placeholder="eduardo.lino@pucpr.br"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="border rounded px-3 py-2"
          />

          <input
            type="password"
            placeholder="******"
            value={senha}
            onChange={(e) => setSenha(e.target.value)}
            className="border rounded px-3 py-2"
          />

          <button
            onClick={validarAcesso}
            className="border rounded px-3 py-2 w-fit hover:bg-gray-50 active:scale-[.99]"
          >
            Acessar
          </button>

          {/* Label/Mensagem */}
          <p>{mensagem}</p>
        </div>
      </div>
    </div>
  );
}
